import logo from './logo.svg';
import './App.css';
import { useQuery, gql, } from "@apollo/client"



function App() {

  const GET_LAUNCHES = gql`
  query GetLaunches {
    launches(limit: 5) {
      launch_date_utc
      launch_success
      rocket {
        rocket_name
      }
      links {
        video_link
      }
      details
    }
  }
`;

const { loading, error, data } = useQuery(GET_LAUNCHES);

if (loading) return <p>Loading...</p>
if (error) return <p>Error :(</p>



  return (
    <div className="App">
      {data}
    </div>
  );
}

export default App;
